class Friendship{constructor(e){this.toggler=e,this.toggleFriendship(this.toggler)}toggleFriendship(e){$(e).on("click",(e=>{e.stopPropagation(),e.preventDefault();let t=this.toggler;$.ajax({type:"POST",url:$(t).attr("href")}).done((e=>{e.data.unfriended&&$(t).parent().remove(),new Noty({text:"Friendship Ended !!",type:"success",theme:"relax",layout:"topRight",timeout:2e3}).show()})).fail((e=>{console.log("Error in completing the request: ",e)}))}))}}try{$(" #friendship_button").each((function(){new Friendship(this)}))}catch(e){console.log(e)}