class ToggleFriendship{constructor(e){this.toggler=e,this.toggleFriendship(this.toggler)}toggleFriendship(e){$(e).on("click",(e=>{e.preventDefault();let t=this.toggler;$.ajax({type:"POST",url:$(t).attr("href")}).done((e=>{let o;o=e.data.unfriended?"Add Friend":"Remove Friend",new Noty({text:e.message,type:"success",theme:"relax",layout:"topRight",timeout:2e3}).show(),$(t).html(o)})).fail((e=>{console.log("Error in completing the request: ",e)}))}))}}try{$(" #friendship_button").each((function(){new ToggleFriendship(this)}))}catch(e){console.log(e)}