class ToggleLike{constructor(t){this.toggler=t,this.toggleLike(this.toggler)}toggleLike(t){$(t).on("click",(t=>{t.preventDefault();let e=this.toggler;$.ajax({type:"POST",url:$(e).attr("href")}).done((t=>{let o=parseInt($(e).attr("data-likes"));t.data.deleted?(o--,$(e).hasClass("comment-like-button")?this.setColor(e,"#b8fff9"):this.setColor(e,"#00547a")):(o++,$(e).hasClass("comment-like-button")?this.setColor(e,"rgb(255, 0, 0)"):this.setColor(e,"rgb(199, 0, 0)")),$(e).attr("data-likes",o),$(e).html(`<i class='fa-solid fa-thumbs-up'></i>&ensp;\n                <span>${o} Like</span>`)})).fail((t=>{console.log("Error in completing the request: ",t)}))}))}setColor(t,e){$(t).css("color",e)}}try{$(" .toggle-like-button").each((function(){new ToggleLike(this)}))}catch(t){console.log(t)}